                    def LinearSVC_MainFunc():

                        global widgets
                        for widget in widgets[:]:
                            widget.destroy()
                            widgets.remove(widget)

                        predicted_value_result = IntVar()
                        predicted_value_result.set("")

                        model_evaluation_and_statistics_result = IntVar()
